    // Booleans are more expensive than uint256 or any type that takes up a full
    // word because each write operation emits an extra SLOAD to first read the
    // slot's contents, replace the bits taken up by the boolean, and then write
    // back. This is the compiler's defense against contract upgrades and
    // pointer aliasing, and it cannot be disabled.

    // The values being non-zero value makes deployment a bit more expensive,
    // but in exchange the refund on every call to nonReentrant will be lower in
    // amount. Since refunds are capped to a percentage of the total
    // transaction's gas, it is best to keep them low in cases like this one, to
    // increase the likelihood of the full refund coming into effect.
    uint256 private constant NOT_ENTERED = 1;
    uint256 private constant ENTERED = 2;

    uint256 private _status;

    /**
     * @dev Unauthorized reentrant call.
     */
    error ReentrancyGuardReentrantCall();

    constructor() {
        _status = NOT_ENTERED;
    }

    /**
     * @dev Prevents a contract from calling itself, directly or indirectly.
     * Calling a `nonReentrant` function from another `nonReentrant`
     * function is not supported. It is possible to prevent this from happening
     * by making the `nonReentrant` function external, and making it call a
     * `private` function that does the actual work.
     */
    modifier nonReentrant() {
        _nonReentrantBefore();
        _;
        _nonReentrantAfter();
    }

    function _nonReentrantBefore() private {
        // On the first call to nonReentrant, _status will be NOT_ENTERED
        if (_status == ENTERED) {
            revert ReentrancyGuardReentrantCall();
        }

        // Any calls to nonReentrant after this point will fail
        _status = ENTERED;
    }

    function _nonReentrantAfter() private {
        // By storing the original value once again, a refund is triggered (see
        // https://eips.ethereum.org/EIPS/eip-2200)
        _status = NOT_ENTERED;
    }

    /**
     * @dev Returns true if the reentrancy guard is currently set to "entered", which indicates there is a
     * `nonReentrant` function in the call stack.
     */
    function _reentrancyGuardEntered() internal view returns (bool) {
        return _status == ENTERED;
    }
}

contracts/EducationalContentMarketplace..content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.22;

import {Ownable} from "@openzeppelin/contracts/access/Ownable.sol";
import {ReentrancyGuard} from "@openzeppelin/contracts/utils/ReentrancyGuard.sol";
import {Pausable} from "@openzeppelin/contracts/utils/Pausable.sol";
import "./MarketplaceCore.sol";
import "./MarketplaceViews.sol";
import "./MarketplaceAdmin.sol";
import "./IEducationalMarketplace.sol";

/**
 * @title Educational Content Marketplace
 * @dev Pure on-chain marketplace for educational content with royalty distribution
 * @notice EduMarket - On-chain educational content trading platform
 */
contract EducationalContentMarketplace is 
    Ownable, 
    ReentrancyGuard, 
    Pausable, 
    MarketplaceCore, 
    MarketplaceViews, 
    MarketplaceAdmin
{

    // Additional events not in core modules
    event ContentPriceUpdated(
        uint256 indexed contentId,
        uint256 oldPrice,
        uint256 newPrice,
        address indexed creator
    );

    constructor(address initialOwner) Ownable(initialOwner) {
        _grantRole(DEFAULT_ADMIN_ROLE, initialOwner);
        _grantRole(MODERATOR_ROLE, initialOwner);
        _grantRole(CONTENT_MANAGER_ROLE, initialOwner);
        _grantRole(EMERGENCY_ROLE, initialOwner);
    }

    /**
     * @dev Create new educational content
     */
    function createContent(
        string calldata _title,
        string calldata _description,
        IEducationalMarketplace.ContentCategory _category,
        bytes32 _contentHash,
        uint256 _price,
        uint256 _customRoyaltyPercent
    ) 
        external 
        nonReentrant 
        whenNotPaused 
        notBlacklisted(msg.sender) 
        notEmergencyStop 
        creationCooldownPassed(msg.sender) 
        returns (uint256) 
    {
        return _createContent(_title, _description, _category, _contentHash, _price, _customRoyaltyPercent);
    }

    /**
     * @dev Add collaborators to content
     */
    function addCollaborators(
        uint256 _contentId,
        address[] calldata _collaborators,
        uint256[] calldata _sharePercents,
        bytes32[] calldata _roleHashes
    ) 
        external 
        validActiveContent(_contentId) 
        onlyContentCreator(_contentId) 
    {
        _addCollaborators(_contentId, _collaborators, _sharePercents, _roleHashes);
    }

    /**
     * @dev Purchase content with automatic royalty distribution
     */
    function purchaseContent(uint256 _contentId) 
        external 
        payable 
        nonReentrant 
        whenNotPaused 
        validActiveContent(_contentId) 
        notBlacklisted(msg.sender) 
        notEmergencyStop 
    {
        _purchaseContent(_contentId);
    }

    /**
     * @dev Update content price
     */
    function updateContentPrice(uint256 _contentId, uint256 _newPrice) 
        external 
        validActiveContent(_contentId) 
        onlyContentCreator(_contentId) 
    {
        if (_newPrice < minContentPrice || _newPrice > maxContentPrice) {
            revert InvalidPrice(_newPrice);
        }

        uint256 oldPrice = contentItems[_contentId].price;
        contentItems[_contentId].price = _newPrice;

        emit ContentPriceUpdated(_contentId, oldPrice, _newPrice, msg.sender);
    }

    /**
     * @dev Toggle content status
     */
    function toggleContentStatus(uint256 _contentId) 
        external 
        onlyContentCreator(_contentId) 
    {
        IEducationalMarketplace.ContentItem storage content = contentItems[_contentId];
        IEducationalMarketplace.ContentStatus oldStatus = content.status;

        if (content.status == IEducationalMarketplace.ContentStatus.Active) {
            content.status = IEducationalMarketplace.ContentStatus.Deactivated;
            totalActiveContent--;
        } else if (content.status == IEducationalMarketplace.ContentStatus.Deactivated) {
            content.status = IEducationalMarketplace.ContentStatus.Active;
            totalActiveContent++;
        }

        emit ContentStatusChanged(_contentId, oldStatus, content.status, msg.sender);
    }

    /**
     * @dev Pause platform
     */
    function pause() external onlyOwner {
        _pause();
    }

    /**
     * @dev Unpause platform
     */
    function unpause() external onlyOwner {
        _unpause();
    }

    /**
     * @dev Generate deterministic content hash
     */
    function generateContentHash(
        address _creator,
        string calldata _title,
        string calldata _description,
        uint256 _timestamp
    ) 
        external 
        pure 
        returns (bytes32) 
    {
        return keccak256(abi.encodePacked(_creator, _title, _description, _timestamp));
    }

    /**
     * @dev Verify content integrity
     */
    function verifyContentIntegrity(
        uint256 _contentId,
        bytes32 _expectedHash
    ) 
        external 
        view 
        returns (bool isValid) 
    {
        if (_contentId >= nextContentId) return false;
        return contentItems[_contentId].contentHash == _expectedHash;
    }

    /**
     * @dev Get purchase by hash
     */
    function getPurchaseByHash(bytes32 _purchaseHash) 
        external 
        view 
        returns (
            address buyer,
            uint256 contentId,
            uint256 blockNumber,
            uint256 pricePaid,
            bool found
        ) 
    {
        for (uint256 i = 0; i < purchases.length; i++) {
            if (purchases[i].purchaseHash == _purchaseHash) {
                IEducationalMarketplace.Purchase memory purchase = purchases[i];
                return (
                    purchase.buyer,
                    purchase.contentId,
                    purchase.blockNumber,
                    purchase.pricePaid,
                    true
                );
            }
        }
        return (address(0), 0, 0, 0, false);
    }

    /**
     * @dev Get recent purchases with pagination
     */
    function getRecentPurchases(uint256 _offset, uint256 _limit) 
        external 
        view 
        returns (IEducationalMarketplace.Purchase[] memory recentPurchases) 
    {
        uint256 totalPurchases = purchases.length;
        if (totalPurchases == 0 || _offset >= totalPurchases) {
            return new IEducationalMarketplace.Purchase[](0);
        }

        uint256 end = _offset + _limit;
        if (end > totalPurchases) {
            end = totalPurchases;
        }

        uint256 count = end - _offset;
        recentPurchases = new IEducationalMarketplace.Purchase[](count);

        // Return in reverse order (most recent first)
        for (uint256 i = 0; i < count; i++) {
            recentPurchases[i] = purchases[totalPurchases - 1 - _offset - i];
        }
    }

    /**
     * @dev Get content statistics
     */
    function getContentStats(uint256 _contentId) 
        external 
        view 
        returns (
            uint32 totalSales,
            uint32 totalViews,
            uint256 totalEarnings,
            uint256 createdAt,
            IEducationalMarketplace.ContentStatus status,
            address creator
        ) 
    {
        if (_contentId >= nextContentId) revert ContentNotFound(_contentId);

        IEducationalMarketplace.ContentItem memory content = contentItems[_contentId];

        return (
            content.totalSales,
            content.totalViews,
            content.totalEarnings,
            content.createdAtBlock,
            content.status,
            content.creator
        );
    }

    /**
     * @dev Get multiple content items by IDs (batch query)
     */
    function getContentBatch(uint256[] calldata _contentIds) 
        external 
        view 
        returns (
            bytes32[] memory titleHashes,
            uint256[] memory prices,
            IEducationalMarketplace.ContentCategory[] memory categories,
            IEducationalMarketplace.ContentStatus[] memory statuses
        ) 
    {
        uint256 length = _contentIds.length;
        titleHashes = new bytes32[](length);
        prices = new uint256[](length);
        categories = new IEducationalMarketplace.ContentCategory[](length);
        statuses = new IEducationalMarketplace.ContentStatus[](length);

        for (uint256 i = 0; i < length; i++) {
            uint256 contentId = _contentIds[i];
            if (contentId < nextContentId) {
                IEducationalMarketplace.ContentItem memory content = contentItems[contentId];
                titleHashes[i] = keccak256(abi.encodePacked(content.title));
                prices[i] = content.price;
                categories[i] = content.category;
                statuses[i] = content.status;
            }
        }
    }

    /**
     * @dev Get creator performance metrics
     */
    function getCreatorMetrics(address _creator) 
        external 
        view 
        returns (
            uint256 totalContentCreated,
            uint256 totalSalesCount,
            uint256 totalEarningsAmount,
            uint256 averageContentPrice,
            uint256 todayUploads
        ) 
    {
        IEducationalMarketplace.Creator storage creator = creators[_creator];

        // Calculate average price from on-chain data
        uint256[] memory creatorContentIds = creatorContent[_creator];
        uint256 totalPrice = 0;
        uint256 activeContentCount = 0;

        for (uint256 i = 0; i < creatorContentIds.length; i++) {
            uint256 contentId = creatorContentIds[i];
            if (contentItems[contentId].status == IEducationalMarketplace.ContentStatus.Active) {
                totalPrice += contentItems[contentId].price;
                activeContentCount++;
            }
        }

        uint256 avgPrice = activeContentCount > 0 ? totalPrice / activeContentCount : 0;

        // Get today's uploads
        uint256 today = block.number / BLOCKS_PER_DAY;
        uint256 todayUploadsCount = dailyUploads[_creator][today];

        return (
            creator.totalContent,
            creator.totalSales,
            creator.totalEarnings,
            avgPrice,
            todayUploadsCount
        );
    }

    // Fallback and receive functions
    receive() external payable {
        emit PlatformFunded(msg.sender, msg.value);
    }

    fallback() external {
        revert("Function not found");
    }
}

contracts/IEducationalMarketplace.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.22;

/**
 * @title IEducationalMarketplace
 * @dev Interface for Educational Content Marketplace
 */
interface IEducationalMarketplace {
    // Enums
    enum ContentStatus { Active, Deactivated, Pending }
    enum ContentCategory { Programming, Mathematics, Science, Language, Business, Art, Other }

    // Structs
    struct ContentItem {
        uint256 contentId;
        string title;
        string description;
        ContentCategory category;
        bytes32 contentHash;
        uint256 price;
        uint256 royaltyPercent;
        ContentStatus status;
        address payable creator;
        address payable currentOwner;
        uint256 createdAtBlock;
        uint32 totalSales;
        uint32 totalViews;
        uint256 totalEarnings;
    }

    struct Creator {
        uint256 totalContent;
        uint256 totalSales;
        uint256 totalEarnings;
        uint256 averageRating;
        uint256 lastContentBlock;
    }

    struct Purchase {
        address buyer;
        uint256 contentId;
        uint256 blockNumber;
        uint256 pricePaid;
        uint256 royaltyPaid;
        uint256 platformFeePaid;
        bytes32 purchaseHash;
    }

    struct Collaborator {
        address collaboratorAddress;
        uint256 sharePercent;
        bytes32 roleHash;
    }

    // Core Functions - only function signatures, no events in interface
    function createContent(
        string calldata title,
        string calldata description,
        ContentCategory category,
        bytes32 contentHash,
        uint256 price,
        uint256 customRoyaltyPercent
    ) external returns (uint256);

    function purchaseContent(uint256 contentId) external payable;

    function addCollaborators(
        uint256 contentId,
        address[] calldata collaborators,
        uint256[] calldata sharePercents,
        bytes32[] calldata roleHashes
    ) external;

    function updateContentPrice(uint256 contentId, uint256 newPrice) external;
}

contracts/MarketplaceAdmin.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.22;

import {Address} from "@openzeppelin/contracts/utils/Address.sol";
import {AccessControl} from "@openzeppelin/contracts/access/AccessControl.sol";
import "./MarketplaceStorage.sol";
import "./MarketplaceErrors.sol";
import "./IEducationalMarketplace.sol";

/**
 * @title MarketplaceAdmin
 * @dev Admin functions for Educational Content Marketplace
 */
contract MarketplaceAdmin is MarketplaceStorage, MarketplaceErrors, AccessControl {
    using Address for address payable;

    // Events
    event ContentStatusChanged(
        uint256 indexed contentId,
        IEducationalMarketplace.ContentStatus oldStatus,
        IEducationalMarketplace.ContentStatus newStatus,
        address indexed moderator
    );

    event EmergencyAction(
        address indexed admin,
        bytes32 actionHash,
        address indexed target,
        uint256 blockNumber
    );

    event PlatformFunded(address indexed funder, uint256 amount);
    event UserBlacklisted(address indexed user, bytes32 reasonHash);
    event UserWhitelisted(address indexed user);

    /**
     * @dev Update platform parameters
     */
    function updatePlatformParameters(
        uint256 _platformFeePercent,
        uint256 _defaultRoyaltyPercent,
        uint256 _maxRoyaltyPercent,
        uint256 _creationCooldownBlocks
    ) external onlyRole(DEFAULT_ADMIN_ROLE) {
        require(_platformFeePercent <= 1000, "Platform fee too high");
        require(_defaultRoyaltyPercent <= 2500, "Default royalty too high");
        require(_maxRoyaltyPercent <= 5000, "Max royalty too high");
        require(_creationCooldownBlocks >= 1 && _creationCooldownBlocks <= 100, "Invalid cooldown");

        platformFeePercent = _platformFeePercent;
        defaultRoyaltyPercent = _defaultRoyaltyPercent;
        maxRoyaltyPercent = _maxRoyaltyPercent;
        creationCooldownBlocks = _creationCooldownBlocks;

        emit EmergencyAction(msg.sender, keccak256("Platform parameters updated"), address(0), block.number);
    }

    /**
     * @dev Update security parameters
     */
    function updateSecurityParameters(
        uint256 _minContentPrice,
        uint256 _maxContentPrice,
        uint256 _dailyUploadLimit,
        uint256 _maxCollaborators
    ) external onlyRole(DEFAULT_ADMIN_ROLE) {
        require(_minContentPrice > 0, "Invalid min price");
        require(_maxContentPrice > _minContentPrice, "Invalid max price");
        require(_dailyUploadLimit > 0 && _dailyUploadLimit <= 50, "Invalid upload limit");
        require(_maxCollaborators > 0 && _maxCollaborators <= 20, "Invalid max collaborators");

        minContentPrice = _minContentPrice;
        maxContentPrice = _maxContentPrice;
        dailyUploadLimit = _dailyUploadLimit;
        maxCollaborators = _maxCollaborators;

        emit EmergencyAction(msg.sender, keccak256("Security parameters updated"), address(0), block.number);
    }

    /**
     * @dev Blacklist user
     */
    function blacklistUser(address _user, bytes32 _reasonHash) 
        external 
        onlyRole(MODERATOR_ROLE) 
    {
        blacklistedUsers[_user] = true;
        emit UserBlacklisted(_user, _reasonHash);
        emit EmergencyAction(msg.sender, _reasonHash, _user, block.number);
    }

    /**
     * @dev Whitelist user
     */
    function whitelistUser(address _user) 
        external 
        onlyRole(MODERATOR_ROLE) 
    {
        blacklistedUsers[_user] = false;
        emit UserWhitelisted(_user);
        emit EmergencyAction(msg.sender, keccak256("User whitelisted"), _user, block.number);
    }

    /**
     * @dev Emergency circuit breaker
     */
    function activateEmergencyStop() external onlyRole(EMERGENCY_ROLE) {
        emergencyStop = true;
        emit EmergencyAction(msg.sender, keccak256("Emergency stop activated"), address(0), block.number);
    }

    /**
     * @dev Reset emergency state
     */
    function resetEmergencyState() external onlyRole(DEFAULT_ADMIN_ROLE) {
        emergencyStop = false;
        emit EmergencyAction(msg.sender, keccak256("Emergency state reset"), address(0), block.number);
    }

    /**
     * @dev Moderate content status
     */
    function moderateContent(uint256 _contentId, IEducationalMarketplace.ContentStatus _newStatus) 
        external 
        onlyRole(MODERATOR_ROLE) 
    {
        if (_contentId >= nextContentId) revert ContentNotFound(_contentId);

        IEducationalMarketplace.ContentItem storage content = contentItems[_contentId];
        IEducationalMarketplace.ContentStatus oldStatus = content.status;

        if (oldStatus == IEducationalMarketplace.ContentStatus.Active && _newStatus != IEducationalMarketplace.ContentStatus.Active) {
            totalActiveContent--;
        } else if (oldStatus != IEducationalMarketplace.ContentStatus.Active && _newStatus == IEducationalMarketplace.ContentStatus.Active) {
            totalActiveContent++;
        }

        content.status = _newStatus;

        emit ContentStatusChanged(_contentId, oldStatus, _newStatus, msg.sender);
        emit EmergencyAction(msg.sender, keccak256("Content moderated"), address(uint160(_contentId)), block.number);
    }

    /**
     * @dev Batch moderate content
     */
    function batchModerateContent(uint256[] calldata _contentIds, IEducationalMarketplace.ContentStatus _newStatus) 
        external 
        onlyRole(MODERATOR_ROLE) 
    {
        require(_contentIds.length <= MAX_BATCH_SIZE, "Batch too large");

        for (uint256 i = 0; i < _contentIds.length; i++) {
            uint256 contentId = _contentIds[i];
            if (contentId < nextContentId) {
                IEducationalMarketplace.ContentItem storage content = contentItems[contentId];
                IEducationalMarketplace.ContentStatus oldStatus = content.status;

                if (oldStatus == IEducationalMarketplace.ContentStatus.Active && _newStatus != IEducationalMarketplace.ContentStatus.Active) {
                    totalActiveContent--;
                } else if (oldStatus != IEducationalMarketplace.ContentStatus.Active && _newStatus == IEducationalMarketplace.ContentStatus.Active) {
                    totalActiveContent++;
                }

                content.status = _newStatus;
                emit ContentStatusChanged(contentId, oldStatus, _newStatus, msg.sender);
            }
        }
    }

    /**
     * @dev Withdraw platform fees
     */
    function withdraw(uint256 _amount) external onlyRole(DEFAULT_ADMIN_ROLE) {
        require(_amount > 0, "Invalid amount");
        require(_amount <= address(this).balance, "Insufficient balance");

        payable(msg.sender).sendValue(_amount);
        emit EmergencyAction(msg.sender, keccak256("Withdrawal"), address(0), block.number);
    }

    /**
     * @dev Fund platform
     */
    function fundPlatform() external payable {
        require(msg.value > 0, "Invalid amount");
        emit PlatformFunded(msg.sender, msg.value);
    }

    /**
     * @dev Get platform security status
     */
    function getSecurityStatus() 
        external 
        view 
        returns (
            bool emergencyActive,
            uint256 platformBalance,
            uint256 totalActiveContentCount,
            uint256 currentBlock
        ) 
    {
        return (
            emergencyStop,
            address(this).balance,
            totalActiveContent,
            block.number
  );
}
}

contracts/MarketplaceCore.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.22;

import {Address} from "@openzeppelin/contracts/utils/Address.sol";
import "./MarketplaceStorage.sol";
import "./MarketplaceErrors.sol";
import "./IEducationalMarketplace.sol";

/**
 * @title MarketplaceCore
 * @dev Core functionality for Educational Content Marketplace
 */
contract MarketplaceCore is MarketplaceStorage, MarketplaceErrors {
    using Address for address payable;

    // Events
    event ContentCreated(
        uint256 indexed contentId,
        address indexed creator,
        bytes32 indexed titleHash,
        uint256 price,
        IEducationalMarketplace.ContentCategory category,
        uint256 blockNumber
    );

    event ContentPurchased(
        address indexed buyer,
        uint256 indexed contentId,
        address indexed seller,
        uint256 price,
        uint256 royaltyPaid,
        bytes32 purchaseHash
    );

    event RoyaltyDistributed(
        uint256 indexed contentId,
        address indexed recipient,
        uint256 amount,
        bytes32 roleHash
    );

    event CollaboratorAdded(
        uint256 indexed contentId,
        address indexed collaborator,
        uint256 sharePercent,
        bytes32 roleHash
    );

    // Modifiers
    modifier validActiveContent(uint256 _contentId) {
        if (_contentId >= nextContentId) revert ContentNotFound(_contentId);
        if (contentItems[_contentId].status != IEducationalMarketplace.ContentStatus.Active) {
            revert ContentNotActive(_contentId);
        }
        _;
    }

    modifier creationCooldownPassed(address _creator) {
        uint256 blocksPassed = block.number - creators[_creator].lastContentBlock;
        if (blocksPassed < creationCooldownBlocks) {
            revert CreationCooldownActive(creationCooldownBlocks - blocksPassed);
        }
        _;
    }

    modifier notBlacklisted(address _user) {
        if (blacklistedUsers[_user]) revert UserIsBlacklisted();
        _;
    }

    modifier notEmergencyStop() {
        if (emergencyStop) revert EmergencyStopActive();
        _;
    }

    modifier onlyContentCreator(uint256 _contentId) {
        if (contentItems[_contentId].creator != msg.sender) revert UnauthorizedAccess();
        _;
    }

    /**
     * @dev Create new educational content
     */
    function _createContent(
        string calldata _title,
        string calldata _description,
        IEducationalMarketplace.ContentCategory _category,
        bytes32 _contentHash,
        uint256 _price,
        uint256 _customRoyaltyPercent
    ) internal returns (uint256) {
        _validateContentData(_title, _description, _price, _customRoyaltyPercent);
        _checkDailyUploadLimit(msg.sender);

        uint256 newContentId = nextContentId;
        nextContentId++;

        uint256 royaltyPercent = _customRoyaltyPercent > 0 ? _customRoyaltyPercent : defaultRoyaltyPercent;

        contentItems[newContentId] = IEducationalMarketplace.ContentItem({
            contentId: newContentId,
            title: _title,
            description: _description,
            category: _category,
            contentHash: _contentHash,
            price: _price,
            royaltyPercent: royaltyPercent,
            status: IEducationalMarketplace.ContentStatus.Active,
            creator: payable(msg.sender),
            currentOwner: payable(msg.sender),
            createdAtBlock: block.number,
            totalSales: 0,
            totalViews: 0,
            totalEarnings: 0
        });

        // Update creator data
        creators[msg.sender].totalContent++;
        creators[msg.sender].lastContentBlock = block.number;
        createdContent[msg.sender][newContentId] = true;

        creatorContent[msg.sender].push(newContentId);
        totalActiveContent++;

        uint256 today = block.number / BLOCKS_PER_DAY;
        dailyUploads[msg.sender][today]++;

        bytes32 titleHash = keccak256(abi.encodePacked(_title));
        emit ContentCreated(newContentId, msg.sender, titleHash, _price, _category, block.number);

        return newContentId;
    }

    /**
     * @dev Add collaborators to content
     */
    function _addCollaborators(
        uint256 _contentId,
        address[] calldata _collaborators,
        uint256[] calldata _sharePercents,
        bytes32[] calldata _roleHashes
    ) internal {
        if (_collaborators.length != _sharePercents.length || _collaborators.length != _roleHashes.length) {
            revert InvalidContentData();
        }
        if (_collaborators.length > maxCollaborators) revert TooManyCollaborators();

        delete contentCollaborators[_contentId];

        uint256 totalShare = 0;
        for (uint256 i = 0; i < _collaborators.length; i++) {
            if (_collaborators[i] == address(0) || _sharePercents[i] == 0) {
                revert InvalidCollaboratorShare();
            }

            contentCollaborators[_contentId].push(IEducationalMarketplace.Collaborator({
                collaboratorAddress: _collaborators[i],
                sharePercent: _sharePercents[i],
                roleHash: _roleHashes[i]
            }));

            totalShare += _sharePercents[i];

            emit CollaboratorAdded(_contentId, _collaborators[i], _sharePercents[i], _roleHashes[i]);
        }

        if (totalShare > BASIS_POINTS) revert InvalidCollaboratorShare();
    }

    /**
     * @dev Purchase content with royalty distribution
     */
    function _purchaseContent(uint256 _contentId) internal {
        IEducationalMarketplace.ContentItem storage content = contentItems[_contentId];

        if (userPurchases[msg.sender][_contentId]) revert ContentAlreadyPurchased(_contentId);
        if (msg.value < content.price) revert InsufficientPayment(content.price, msg.value);

        uint256 totalPayment = content.price;

        uint256 platformFee = (totalPayment * platformFeePercent) / BASIS_POINTS;
        uint256 royaltyAmount = (totalPayment * content.royaltyPercent) / BASIS_POINTS;
        uint256 sellerAmount = totalPayment - platformFee - royaltyAmount;

        userPurchases[msg.sender][_contentId] = true;
        userPurchaseHistory[msg.sender].push(_contentId);

        content.totalSales++;
        content.totalEarnings += totalPayment;
        totalSales++;
        totalRevenue += totalPayment;

        creators[content.creator].totalSales++;
        creators[content.creator].totalEarnings += sellerAmount + royaltyAmount;

        bytes32 purchaseHash = keccak256(abi.encodePacked(
            msg.sender, 
            _contentId, 
            block.number, 
            totalPayment,
            block.prevrandao
        ));

        purchases.push(IEducationalMarketplace.Purchase({
            buyer: msg.sender,
            contentId: _contentId,
            blockNumber: block.number,
            pricePaid: totalPayment,
            royaltyPaid: royaltyAmount,
            platformFeePaid: platformFee,
            purchaseHash: purchaseHash
        }));

        _distributeRoyalties(_contentId, royaltyAmount);

        if (sellerAmount > 0) {
            content.currentOwner.sendValue(sellerAmount);
        }

        if (msg.value > totalPayment) {
            payable(msg.sender).sendValue(msg.value - totalPayment);
        }

        emit ContentPurchased(msg.sender, _contentId, content.currentOwner, totalPayment, royaltyAmount, purchaseHash);
    }

    /**
     * @dev Distribute royalties to creator and collaborators
     */
    function _distributeRoyalties(uint256 _contentId, uint256 _totalRoyalty) internal {
        IEducationalMarketplace.ContentItem memory content = contentItems[_contentId];
        IEducationalMarketplace.Collaborator[] memory collaborators = contentCollaborators[_contentId];

        if (collaborators.length == 0) {
            if (_totalRoyalty > 0) {
                content.creator.sendValue(_totalRoyalty);
                emit RoyaltyDistributed(_contentId, content.creator, _totalRoyalty, keccak256("Creator"));
            }
        } else {
            uint256 totalCollaboratorShare = 0;

            for (uint256 i = 0; i < collaborators.length; i++) {
                uint256 collaboratorAmount = (_totalRoyalty * collaborators[i].sharePercent) / BASIS_POINTS;
                if (collaboratorAmount > 0) {
                    payable(collaborators[i].collaboratorAddress).sendValue(collaboratorAmount);
                    emit RoyaltyDistributed(_contentId, collaborators[i].collaboratorAddress, collaboratorAmount, collaborators[i].roleHash);
                }
                totalCollaboratorShare += collaborators[i].sharePercent;
            }

            uint256 creatorShare = BASIS_POINTS - totalCollaboratorShare;
            uint256 creatorAmount = (_totalRoyalty * creatorShare) / BASIS_POINTS;
            if (creatorAmount > 0) {
                content.creator.sendValue(creatorAmount);
                emit RoyaltyDistributed(_contentId, content.creator, creatorAmount, keccak256("Creator"));
            }
        }
    }

    /**
     * @dev Validate content data
     */
    function _validateContentData(
        string calldata _title,
        string calldata _description,
        uint256 _price,
        uint256 _royaltyPercent
    ) internal view {
        if (bytes(_title).length < MIN_TITLE_LENGTH || bytes(_title).length > MAX_TITLE_LENGTH) {
            revert InvalidContentData();
        }
        if (bytes(_description).length < minDescriptionLength || bytes(_description).length > maxDescriptionLength) {
            revert InvalidContentData();
        }
        if (_price < minContentPrice || _price > maxContentPrice) {
            revert InvalidPrice(_price);
        }
        if (_royaltyPercent > maxRoyaltyPercent) {
            revert InvalidRoyaltyPercent(_royaltyPercent);
        }
    }

    /**
     * @dev Check daily upload limit
     */
    function _checkDailyUploadLimit(address _creator) internal view {
        uint256 today = block.number / BLOCKS_PER_DAY;
        uint256 todayUploads = dailyUploads[_creator][today];

        if (todayUploads >= dailyUploadLimit) {
            revert DailyLimitExceeded(todayUploads, dailyUploadLimit);
 }
}
}

contracts/MarketplaceErrors.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.22;

/**
 * @title MarketplaceErrors
 * @dev Custom errors for Educational Content Marketplace
 */
contract MarketplaceErrors {
    // Content errors
    error ContentNotFound(uint256 contentId);
    error ContentNotActive(uint256 contentId);
    error ContentAlreadyPurchased(uint256 contentId);
    error InvalidContentData();

    // User access errors
    error UserIsBlacklisted();
    error UnauthorizedAccess();
    error CreationCooldownActive(uint256 remainingBlocks);

    // System errors
    error EmergencyStopActive();
    error InvalidPrice(uint256 price);
    error InvalidRoyaltyPercent(uint256 percent);
    error InsufficientPayment(uint256 required, uint256 provided);

    // Limit errors
    error DailyLimitExceeded(uint256 current, uint256 limit);
    error TooManyCollaborators();
    error InvalidCollaboratorShare();
}

contracts/MarketplaceStorage.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.22;

import "./IEducationalMarketplace.sol";

/**
 * @title MarketplaceStorage
 * @dev Storage contract for Educational Content Marketplace
 */
contract MarketplaceStorage {

    // Role definitions
    bytes32 public constant MODERATOR_ROLE = keccak256("MODERATOR_ROLE");
    bytes32 public constant CONTENT_MANAGER_ROLE = keccak256("CONTENT_MANAGER_ROLE");
    bytes32 public constant EMERGENCY_ROLE = keccak256("EMERGENCY_ROLE");

    // State variables
    mapping(uint256 => IEducationalMarketplace.ContentItem) public contentItems;
    mapping(address => IEducationalMarketplace.Creator) public creators;
    mapping(address => mapping(uint256 => bool)) public userPurchases;
    mapping(address => bool) public blacklistedUsers;
    mapping(uint256 => IEducationalMarketplace.Collaborator[]) public contentCollaborators;
    mapping(address => uint256[]) public creatorContent;
    mapping(address => uint256[]) public userPurchaseHistory;
    IEducationalMarketplace.Purchase[] public purchases;

    // Content management
    uint256 public nextContentId;
    uint256 public totalActiveContent;
    uint256 public totalSales;
    uint256 public totalRevenue;

    // Platform parameters
    uint256 public platformFeePercent = 250; // 2.5%
    uint256 public defaultRoyaltyPercent = 1000; // 10%
    uint256 public maxRoyaltyPercent = 5000; // 50%
    uint256 public minContentPrice = 1000000000000000; // 0.001 ETH
    uint256 public maxContentPrice = 10000000000000000000; // 10 ETH
    uint256 public creationCooldownBlocks = 10;
    uint256 public minDescriptionLength = 50;
    uint256 public maxDescriptionLength = 1000;

    // Security parameters
    uint256 public dailyUploadLimit = 10;
    uint256 public maxCollaborators = 10;
    mapping(address => mapping(uint256 => uint256)) public dailyUploads;

    // Constants
    uint256 public constant BASIS_POINTS = 10000;
    uint256 public constant MIN_TITLE_LENGTH = 5;
    uint256 public constant MAX_TITLE_LENGTH = 100;
    uint256 public constant MAX_BATCH_SIZE = 20;
    uint256 public constant BLOCKS_PER_DAY = 5760;

    // Circuit breakers
    bool public emergencyStop = false;

    // Additional mappings for creator tracking
    mapping(address => mapping(uint256 => bool)) internal createdContent;
    mapping(address => mapping(address => bool)) internal purchasedFrom;
}

contracts/MarketplaceViews.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.22;

import "./MarketplaceStorage.sol";
import "./MarketplaceErrors.sol";
import "./IEducationalMarketplace.sol";

/**
 * @title MarketplaceViews
 * @dev View functions for Educational Content Marketplace
 */
contract MarketplaceViews is MarketplaceStorage, MarketplaceErrors {

    /**
     * @dev Get content details
     */
    function getContentDetails(uint256 _contentId) 
        external 
        view 
        returns (
            string memory title,
            string memory description,
            IEducationalMarketplace.ContentCategory category,
            bytes32 contentHash,
            uint256 price,
            uint256 royaltyPercent,
            IEducationalMarketplace.ContentStatus status,
            address creator,
            address currentOwner,
            uint32 totalSales,
            uint256 createdAt
        ) 
    {
        if (_contentId >= nextContentId) revert ContentNotFound(_contentId);
        IEducationalMarketplace.ContentItem memory content = contentItems[_contentId];

        return (
            content.title,
            content.description,
            content.category,
            content.contentHash,
            content.price,
            content.royaltyPercent,
            content.status,
            content.creator,
            content.currentOwner,
            content.totalSales,
            content.createdAtBlock
        );
    }

    /**
     * @dev Get content hash (access controlled)
     */
    function getContentHash(uint256 _contentId) 
        external 
        view 
        returns (bytes32) 
    {
        if (_contentId >= nextContentId) revert ContentNotFound(_contentId);

        IEducationalMarketplace.ContentItem memory content = contentItems[_contentId];
        if (!userPurchases[msg.sender][_contentId] && 
            content.creator != msg.sender && 
            content.currentOwner != msg.sender) {
            revert UnauthorizedAccess();
        }

        return content.contentHash;
    }

    /**
     * @dev Get content collaborators
     */
    function getContentCollaborators(uint256 _contentId) 
        external 
        view 
        returns (
            address[] memory collaboratorAddresses,
            uint256[] memory sharePercents,
            bytes32[] memory roleHashes
        ) 
    {
        if (_contentId >= nextContentId) revert ContentNotFound(_contentId);

        IEducationalMarketplace.Collaborator[] memory collaborators = contentCollaborators[_contentId];
        collaboratorAddresses = new address[](collaborators.length);
        sharePercents = new uint256[](collaborators.length);
        roleHashes = new bytes32[](collaborators.length);

        for (uint256 i = 0; i < collaborators.length; i++) {
            collaboratorAddresses[i] = collaborators[i].collaboratorAddress;
            sharePercents[i] = collaborators[i].sharePercent;
            roleHashes[i] = collaborators[i].roleHash;
        }
    }

    /**
     * @dev Get creator statistics
     */
    function getCreatorStats(address _creator) 
        external 
        view 
        returns (
            uint256 totalContent,
            uint256 totalSales,
            uint256 totalEarnings,
            uint256 averageRating,
            uint256 lastContentBlock
        ) 
    {
        IEducationalMarketplace.Creator storage creator = creators[_creator];
        return (
            creator.totalContent,
            creator.totalSales,
            creator.totalEarnings,
            creator.averageRating,
            creator.lastContentBlock
        );
    }

    /**
     * @dev Check if user purchased content
     */
    function hasPurchased(address _user, uint256 _contentId) 
        external 
        view 
        returns (bool) 
    {
        return userPurchases[_user][_contentId];
    }

    /**
     * @dev Get marketplace statistics
     */
    function getMarketplaceStats() 
        external 
        view 
        returns (
            uint256 totalContent,
            uint256 activeContent,
            uint256 totalSalesCount,
            uint256 totalRevenueAmount,
            uint256 currentBlock
        ) 
    {
        return (
            nextContentId,
            totalActiveContent,
            totalSales,
            totalRevenue,
            block.number
        );
    }

    /**
     * @dev Get active content IDs with pagination
     */
    function getActiveContentIds(uint256 _offset, uint256 _limit) 
        external 
        view 
        returns (uint256[] memory contentIds, uint256 totalActive) 
    {
        uint256[] memory activeIds = new uint256[](_limit);
        uint256 count = 0;
        uint256 activeCount = 0;

        for (uint256 i = 0; i < nextContentId && count < _limit; i++) {
            if (contentItems[i].status == IEducationalMarketplace.ContentStatus.Active) {
                if (activeCount >= _offset) {
                    activeIds[count] = i;
                    count++;
                }
                activeCount++;
            }
        }

        uint256[] memory result = new uint256[](count);
        for (uint256 i = 0; i < count; i++) {
            result[i] = activeIds[i];
        }

        return (result, activeCount);
    }

    /**
     * @dev Get content by category
     */
    function getContentByCategory(
        IEducationalMarketplace.ContentCategory _category, 
        uint256 _offset, 
        uint256 _limit
    ) 
        external 
        view 
        returns (uint256[] memory contentIds, uint256 totalMatching) 
    {
        uint256[] memory matchingIds = new uint256[](_limit);
        uint256 count = 0;
        uint256 matchingCount = 0;

        for (uint256 i = 0; i < nextContentId && count < _limit; i++) {
            if (contentItems[i].status == IEducationalMarketplace.ContentStatus.Active && 
                contentItems[i].category == _category) {
                if (matchingCount >= _offset) {
                    matchingIds[count] = i;
                    count++;
                }
                matchingCount++;
            }
        }

        uint256[] memory result = new uint256[](count);
        for (uint256 i = 0; i < count; i++) {
            result[i] = matchingIds[i];
        }

        return (result, matchingCount);
    }

    /**
     * @dev Get content by price range
     */
    function getContentByPriceRange(
        uint256 _minPrice,
        uint256 _maxPrice,
        uint256 _offset,
        uint256 _limit
    ) 
        external 
        view 
        returns (uint256[] memory contentIds, uint256 totalMatching) 
    {
        uint256[] memory matchingIds = new uint256[](_limit);
        uint256 count = 0;
        uint256 matchingCount = 0;

        for (uint256 i = 0; i < nextContentId && count < _limit; i++) {
            if (contentItems[i].status == IEducationalMarketplace.ContentStatus.Active && 
                contentItems[i].price >= _minPrice && 
                contentItems[i].price <= _maxPrice) {
                if (matchingCount >= _offset) {
                    matchingIds[count] = i;
                    count++;
                }
                matchingCount++;
            }
        }

        uint256[] memory result = new uint256[](count);
        for (uint256 i = 0; i < count; i++) {
            result[i] = matchingIds[i];
        }

        return (result, matchingCount);
    }

    /**
     * @dev Get trending content by sales
     */
    function getTrendingContentIds(uint256 _limit) 
        external 
        view 
        returns (uint256[] memory contentIds) 
    {
        uint256[] memory activeIds = new uint256[](totalActiveContent);
        uint256 activeCount = 0;

        for (uint256 i = 0; i < nextContentId; i++) {
            if (contentItems[i].status == IEducationalMarketplace.ContentStatus.Active) {
                activeIds[activeCount] = i;
                activeCount++;
            }
        }

        // Sort by total sales
        for (uint256 i = 0; i < activeCount; i++) {
            for (uint256 j = i + 1; j < activeCount; j++) {
                if (contentItems[activeIds[i]].totalSales < contentItems[activeIds[j]].totalSales) {
                    uint256 temp = activeIds[i];
                    activeIds[i] = activeIds[j];
                    activeIds[j] = temp;
                }
            }
        }

        uint256 resultCount = _limit > activeCount ? activeCount : _limit;
        contentIds = new uint256[](resultCount);

        for (uint256 i = 0; i < resultCount; i++) {
            contentIds[i] = activeIds[i];
        }
    }

    /**
     * @dev Get user's purchase history
     */
    function getUserPurchaseHistory(address _user) 
        external 
        view 
        returns (uint256[] memory contentIds) 
    {
        return userPurchaseHistory[_user];
    }

    /**
     * @dev Get creator's content
     */
    function getCreatorContent(address _creator) 
        external 
        view 
        returns (uint256[] memory) 
    {
        return creatorContent[_creator];
    }

    /**
     * @dev Check if user can create content
     */
    function canUserCreateContent(address _user) 
        external 
        view 
        returns (bool canCreate, bytes32 statusHash) 
    {
        if (emergencyStop) {
            return (false, keccak256("Emergency stop active"));
        }

        if (blacklistedUsers[_user]) {
            return (false, keccak256("User blacklisted"));
        }

        uint256 blocksPassed = block.number - creators[_user].lastContentBlock;
        if (blocksPassed < creationCooldownBlocks) {
            return (false, keccak256("Creation cooldown active"));
        }

        uint256 today = block.number / BLOCKS_PER_DAY;
        if (dailyUploads[_user][today] >= dailyUploadLimit) {
            return (false, keccak256("Daily upload limit reached"));
        }

        return (true, keccak256("Ready"));
    }

    /**
     * @dev Get daily upload status
     */
    function getDailyUploadStatus(address _user) 
        external 
        view 
        returns (uint256 todayUploads, uint256 remainingUploads) 
    {
        uint256 today = block.number / BLOCKS_PER_DAY;
        todayUploads = dailyUploads[_user][today];
        remainingUploads = todayUploads >= dailyUploadLimit ? 0 : dailyUploadLimit - todayUploads;
    }

    /**
     * @dev Calculate purchase preview
     */
    function calculatePurchasePreview(uint256 _contentId) 
        external 
        view 
        returns (
            uint256 contentPrice,
            uint256 platformFee,
            uint256 royaltyAmount,
            uint256 sellerAmount,
            uint256 totalRequired
        ) 
    {
        if (_contentId >= nextContentId) revert ContentNotFound(_contentId);

        IEducationalMarketplace.ContentItem memory content = contentItems[_contentId];
        contentPrice = content.price;
        platformFee = (contentPrice * platformFeePercent) / BASIS_POINTS;
        royaltyAmount = (contentPrice * content.royaltyPercent) / BASIS_POINTS;
        sellerAmount = contentPrice - platformFee - royaltyAmount;
        totalRequired = contentPrice;
    }

    /**
     * @dev Get user's creation cooldown status
     */
    function getUserCooldown(address _user) 
        external 
        view 
        returns (
            uint256 blocksRemaining,
            bool canCreate
        ) 
    {
        uint256 blocksPassed = block.number - creators[_user].lastContentBlock;

        if (blocksPassed >= creationCooldownBlocks) {
            return (0, true);
        } else {
            return (creationCooldownBlocks - blocksPassed, false);
 }
}
}

